SC.SWIPE_HORIZONTAL = "X";
SC.SWIPE_VERTICAL = "Y";
SC.SWIPE_ANY = "XY";
SC.SWIPE_LEFT = "LEFT";
SC.SWIPE_RIGHT = "RIGHT";
SC.SWIPE_UP = "UP";
SC.SWIPE_DOWN = "DOWN";

