SC.SelectView.LARGE_OFFSET_X = -17;
SC.SelectView.LARGE_OFFSET_Y = 6;
SC.SelectView.LARGE_POPUP_MENU_WIDTH_OFFSET = 3;

SC.SelectView.HUGE_OFFSET_X = 0;
